mapper.insert:
insert dm_user_role(id, user_id, role_id) (
select REPLACE(UUID(), '-', ''), #{userId}, id
from dm_role where dm_role.name in

    #{item}

)

mapper.update:
update dm_user
set password = #{encodedPassword}
where id = #{userId}

update dm_user
set username = #{username},
    password = #{password}
where id = #{id}

mapper.select:
select dc.*
from dm_collection dc
where user_id = #{userId}

select *
from dm_paper
         right join dm_collection dc on dm_paper.id = dc.paper_id and dc.user_id = #{userId}

select *
from dm_journal
         right join dm_subscription ds on dm_journal.id = ds.journal_id and ds.user_id = ${userId}
